mapper.select:
WITH up_val as( SELECT station_id,updown_flag,MAX(max_rate) AS yval FROM stat_info WHERE 1=1
AND DATE_FORMAT(key_date, '%Y-%m') = #{keyDate}
AND DATE_FORMAT(key_date, '%Y') = #{keyDate}
AND KEY_DATE=#{keyDate}
and updown_flag='U' GROUP BY station_id,updown_flag )
,down_val as( SELECT station_id,updown_flag,MAX(max_rate) AS yval2 FROM stat_info WHERE 1=1
AND DATE_FORMAT(key_date, '%Y-%m') = #{keyDate}
AND DATE_FORMAT(key_date, '%Y') = #{keyDate}
AND KEY_DATE=#{keyDate}
and updown_flag='D' GROUP BY station_id,updown_flag )
SELECT j1.*,IFNULL(c.yval2,0) AS yVal2 FROM ( SELECT a.station_id,a.up_num,a.down_num,a.station_name as xVal,IFNULL(b.yval,0) AS yVal FROM station_info a LEFT JOIN up_val b ON ( b.station_id=a.up_num or b.station_id=a.down_num ) )j1
LEFT JOIN down_val c ON ( c.station_id=j1.up_num or c.station_id=j1.down_num )

WITH am_rh as ( SELECT A.*,B.STATION_NAME FROM ( SELECT DATE_FORMAT(key_date,'%Y-%m') AS key_date ,week_flag,updown_flag, time_zone,station_id AS STID ,avg_rate , max(max_rate) as max_rate ,MAX(rcv_dt) AS rcv_dt from stat_info WHERE 1=1
AND DATE_FORMAT(key_date, '%Y-%m') = #{keyDate}
AND DATE_FORMAT(key_date, '%Y') = #{keyDate}
AND KEY_DATE=#{keyDate}
AND time_zone='AM' group BY
DATE_FORMAT(key_date, '%Y-%m')
DATE_FORMAT(key_date, '%Y')
KEY_DATE
,week_flag,updown_flag )A right join station_info B ON ( a.STID=b.up_num or a.STID=b.down_num ) ),
pm_rh as (
SELECT A.*,B.STATION_NAME FROM ( SELECT DATE_FORMAT(key_date,'%Y-%m') AS key_date ,week_flag,updown_flag, time_zone,station_id AS STID , max(max_rate) as max_rate ,avg_rate ,MAX(rcv_dt) AS rcv_dt from stat_info WHERE 1=1
AND DATE_FORMAT(key_date, '%Y-%m') = #{keyDate}
AND DATE_FORMAT(key_date, '%Y') = #{keyDate}
AND KEY_DATE=#{keyDate}
AND time_zone='PM' group BY
DATE_FORMAT(key_date, '%Y-%m')
DATE_FORMAT(key_date, '%Y')
KEY_DATE
,week_flag,updown_flag )A right JOIN station_info B ON ( a.STID=b.up_num or a.STID=b.down_num )
)
SELECT a.key_date AS key_date ,CASE WHEN a.week_flag= 'D' THEN '평일' ELSE '주말' END AS week_flag ,CASE WHEN a.updown_flag= 'D' THEN '하선' ELSE '상선' END AS updown_flag ,a.max_rate AS am_max_rate ,a.rcv_dt AS am_rcv_dt ,a.STID AS am_station_id ,a.STATION_NAME AS am_station_name ,a.avg_rate AS am_avg_rate ,p.max_rate AS pm_max_rate ,p.rcv_dt AS pm_rcv_dt ,p.STID AS pm_station_id ,p.STATION_NAME AS pm_station_name ,p.avg_rate AS pm_avg_rate FROM am_rh a LEFT JOIN pm_rh p ON a.week_flag=p.week_flag AND a.updown_flag=p.updown_flag AND a.STID=p.STID where a.key_date is not null
