mapper.select:
WITH tcms AS(
SELECT * FROM tcms_log WHERE rcv_dt>= now() - interval 1 minute and formation_no>=201 AND formation_no<=300 AND kpa1>50 and kpa2>50 and kpa3>50 and kpa4>50 and station_id >= 0 AND station_id<60
), jjng as(
SELECT joong2.formation_no AS jfno FROM ( SELECT tt.formation_no ,tt.device_mac FROM ( SELECT rcv_dt,formation_no,station_id ,device_mac ,COUNT(*) AS cnt FROM tcms_log WHERE 1=1 and rcv_dt>= now() - INTERVAL 2 minute and formation_no>=201 AND formation_no<=243 AND kpa1>50 and kpa2>50 and kpa3>50 and kpa4>50 and station_id >= 0 AND station_id<60 GROUP BY formation_no,device_mac ORDER BY formation_no,device_mac )tt WHERE (tt.formation_no) IN( SELECT formation_no FROM ( SELECT jng.*,COUNT(*) AS cnt2 from ( SELECT rcv_dt,formation_no,station_id ,device_mac FROM ( SELECT rcv_dt,formation_no,station_id ,device_mac ,COUNT(*) AS cnt FROM tcms_log WHERE 1=1 and rcv_dt>= now() - interval 2 minute and formation_no>=201 AND formation_no<=243 AND kpa1>50 and kpa2>50 and kpa3>50 and kpa4>50 and station_id >= 0 AND station_id<60 GROUP BY formation_no,device_mac ORDER BY formation_no,device_mac )tcms GROUP BY formation_no,device_mac ORDER BY formation_no,device_mac )jng GROUP BY formation_no ORDER BY formation_no )t1 WHERE t1.cnt2>1 ) )joong1 LEFT JOIN tcms_info joong2 ON joong1.device_mac=joong2.device_mac
)
SELECT J2.* ,IFNULL(D.station_name ,'현재 미운행') as station_name from ( SELECT J1.* ,case when J1.RATE1 is null then 0 when J1.RATE1 < C.care_st then 1 when J1.RATE1 < C.cwd_st then 2 when J1.RATE1 < C.seve_st then 3 ELSE 4 END AS cwd1 ,case when J1.RATE2 is null then 0 when J1.RATE2 < C.care_st then 1 when J1.RATE2 < C.cwd_st then 2 when J1.RATE2 < C.seve_st then 3 ELSE 4 END AS cwd2 from ( SELECT a.* ,b.*
,if(b.rate_t1 < 0,0,b.rate_t1) AS rate1 ,if(b.rate_t2 < 0,0,b.rate_t2) AS rate2 FROM ASP_INFO a left join ( SELECT TRAIN_NO AS tno2 ,formation_no AS fno2
,rcv_dt
,case when station_id=0 then '' else case when station_id%2=1 then '(상)' when station_id%2=0 then '(하)'ELSE '' END end AS active_cap ,train_cnt ,ROUND(calc_kpa((kpa1+kpa2)/2,formation_no,1)*range_kpa((kpa1+kpa2)/2,formation_no),1) AS rate_t1 ,ROUND(calc_kpa((kpa3+kpa4)/2,formation_no,2)*range_kpa((kpa3+kpa4)/2,formation_no),1) AS rate_t2 ,station_id ,if(a.formation_no=b.jfno,1,0) as train_add_cnt FROM tcms a left join jjng b ON a.formation_no=b.jfno
where (formation_no, rcv_dt) in (
select formation_no, max(rcv_dt) as rcv_dt from tcms group by formation_no
) ) b ON a.formation_no=b.fno2 WHERE 1=1
AND formation_no=#{formationNo}
)J1 ,asp_rate_info C )J2 left join station_info D on ( J2.station_id=D.up_num or J2.station_id=D.down_num ) ORDER BY J2.train_add_cnt DESC, J2.FORMATION_NO

SELECT J1.*,c.* FROM ( SELECT a.formation_no ,IFNULL(a.device_ip,'정보없음') AS device_ip ,IFNULL(a.device_mac,'정보없음') AS device_mac ,IFNULL(a.INS_LOC_TXT,'정보없음') AS INS_LOC_TXT ,IFNULL(a.device_used,'정보없음') AS device_used ,IFNULL(a.gateway_ip,'정보없음') AS gateway_ip ,IFNULL(a.device_user,'정보없음') AS device_user ,a.car_type ,a.etc ,DATE_FORMAT(a.reg_dt, '%Y-%m-%d') as reg_dt FROM TCMS_INFO a )J1 LEFT JOIN asp_info C ON J1.formation_no=c.formation_no WHERE 1=1
AND J1.formation_no=#{formationNo}
AND J1.reg_dt >= #{sDate} AND J1.reg_dt <= #{eDate}
AND J1.car_type =#{carType}
AND J1.formation_no LIKE CONCAT('%',#{schWord},'%')
AND J1.device_user LIKE CONCAT('%',#{schWord},'%')
AND J1.device_used LIKE CONCAT('%',#{schWord},'%')
ORDER BY J1.FORMATION_NO

mapper.update:
UPDATE TCMS_INFO
SET
	  train_no=#{trainNo}
	 ,car_type=#{carType}
	 ,formation_no=#{formationNo}
	 ,device_ip=#{deviceIp}
	 ,device_mac=#{deviceMac}
	 ,INS_LOC_TXT=#{insLocTxt}
	 ,device_used=#{deviceUsed}
	 ,gateway_ip=#{gatewayIp}
	 ,device_user=#{deviceUser}
	 ,etc=#{etc}
WHERE formation_no=#{formationNo}

mapper.delete:
		DELETE FROM TCMS_INFO 
		WHERE formation_no IN
		
            #{item}

mapper.select:
WITH tt AS ( SELECT DATE_FORMAT(RCV_DT,'%H:%i:%s') as RCV_DT ,FORMATION_NO, kpa1,kpa2,kpa3,kpa4 ,(kpa1+kpa2)/2 AS AVG1 ,(kpa3+kpa4)/2 AS AVG2 ,STATION_ID FROM tcms_log_all WHERE 1=1
AND DATE_FORMAT(RCV_dT,'%Y-%m-%d %H:%i:%s') >= #{sDate} AND DATE_FORMAT(RCV_dT,'%Y-%m-%d %H:%i:%s') <= #{eDate}
AND DATE_FORMAT(RCV_dT,'%Y-%m-%d %H:%i:%s') >= concat(curdate()-interval 1 DAY," 07:10:00") AND DATE_FORMAT(RCV_dT,'%Y-%m-%d %H:%i:%s') <= concat(curdate()-interval 1 DAY," 07:20:00")
and formation_no=#{formationNo}
and formation_no>=201 AND formation_no<=300
and station_id%2=1
and station_id%2=0
and station_id >= 3 AND station_id<=56 AND kpa1>50 and kpa2>50 and kpa3>50 and kpa4>50
) SELECT T3.* FROM (
SELECT T2.*,B.station_name ,if(T2.station_id%2=1,'상행','하행') AS active_cap FROM ( SELECT T1.* ,round(calc_kpa(AVG1,formation_no,1),2) AS cal1 ,round(calc_kpa(AVG2,formation_no,2),2) AS cal2 ,round(range_kpa(AVG1,formation_no),2) AS range1 ,round(range_kpa(AVG2,formation_no),2) AS range2
,ROUND(calc_kpa(AVG1,formation_no,1)*range_kpa(AVG1,formation_no),1) AS rate1 ,ROUND(calc_kpa(AVG2,formation_no,2)*range_kpa(AVG2,formation_no),1) AS rate2 FROM ( SELECT a1.* from ( SELECT tt.*, COUNT(*) AS cnt FROM tt GROUP BY station_id,kpa1,kpa2,kpa3,kpa4 )a1 WHERE 1=1
and a1.cnt>=#{dufCnt}
and a1.cnt>=2
)T1 )T2 LEFT JOIN station_info B ON ( T2.station_id=B.up_num or T2.station_id=B.down_num )
)T3 ORDER BY active_cap,formation_no,rcv_dt
