mapper.select:
SELECT J1.*, CASE WHEN J2.rtime2 IS NULL then 'f' WHEN J2.NOW_STATUS='R' THEN 'reset' WHEN J2.NOW_STATUS='U' THEN 'update' WHEN J2.rtime2 < NOW() -INTERVAL 3 MINUTE then 'obs' WHEN J2.`LTE-R_RSSI_VAL` < (SELECT WEAK_VAL FROM TBL_RSSI_STRENGTH) THEN 'weak' WHEN J2.`LTE-R_RSRP_VAL` < (SELECT WEAK_VAL FROM TBL_RSRP_STRENGTH) THEN 'weak' WHEN J2.`LTE-R_RSRQ_VAL` < (SELECT WEAK_VAL FROM TBL_RSRQ_STRENGTH) THEN 'weak' ELSE 'N' END AS OBS_COUNT FROM ( SELECT A.*,B.* FROM ( SELECT `LTE-R_IMEI` ,`LTE-R_IMSI` ,`LTE-R_SERIAL` ,`LTE-R_USER_ID` ,`LTE-R_MAC_ADD` ,`LTE-R_IP` ,`LTE-R_SIM_NO` ,`CON_SYSTEM01_IP` ,`CON_SYSTEM01` ,`CON_SYSTEM02_IP` ,`CON_SYSTEM02` ,`CON_SYSTEM03_IP` ,`CON_SYSTEM03` ,`CON_GW` ,`LTE-R_USED` ,`INS_LOC_TXT` ,`LTE-R_CODE` ,`LTE-R_DEVICE_TYPE` ,`SW_VER_CODE` ,`TEAM_CNT` ,`DEVICE_CNT` ,`LTE-R_CELL_ID` ,`RMT_RESET_YDT` ,`RMT_RESET_RLT` ,`RMT_UPT_YDT` ,`RMT_UPT_RLT` ,`GET_DEVICE_INFO_YDT` ,`SW_VER_UPDATE` ,`USE_YN` ,DATE_FORMAT(REG_YDT ,'%Y-%m-%d') AS REG_YDT ,`INTERVAL_TIME` FROM tbl_router_info WHERE 1=1
AND `LTE-R_IP` LIKE CONCAT('%',#{lteRIp},'%')
AND `LTE-R_MAC_ADD` LIKE CONCAT('%',#{lteRMacAdd},'%')
AND `LTE-R_USED` LIKE CONCAT('%',#{lteRUsed},'%')
AND `INS_LOC_TXT` LIKE CONCAT('%',#{insLocTxt},'%')
AND `DEVICE_CNT` LIKE CONCAT('%',#{deviceCnt},'%')
) A LEFT JOIN ( SELECT DEPART_CODE ,DEPART_NAME ,CPY_NAME ,CPY_CODE ,HQ_NAME ,HQ_CODE ,TEAM_NAME ,TEAM_CODE ,REG_YDT AS DEPART_DATE FROM tbl_depart_info )B ON(SUBSTR(A.`LTE-R_CODE`,1,11)=B.DEPART_CODE) )J1 LEFT JOIN ( SELECT `LTE-R_IP` AS ip2 ,receive_time AS rtime2 ,`LTE-R_RSSI_VAL` ,`LTE-R_RSRP_VAL` ,`LTE-R_RSRQ_VAL` ,`NOW_STATUS` FROM tbl_last_data )J2 ON(J1.`LTE-R_IP`=J2.ip2) WHERE 1=1
AND J1.DEPART_CODE=#{departCode}
AND SUBSTR(J1.DEPART_CODE,9,3)=#{teamCode}
ORDER BY J1.`LTE-R_USED` ASC, J1.`INS_LOC_TXT` ASC
LIMIT 42 OFFSET #{startNum}

SELECT `lte-r_ip`, receive_time, `lte-r_operation_tm`
FROM tbl_operation_time
WHERE `lte-r_ip` = #{lteRIp} 
AND receive_time BETWEEN DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 6 MONTH), '%Y-%m-%d') 
AND DATE_FORMAT(CURDATE(), '%Y-%m-%d')
ORDER BY receive_time;

SELECT SUBSTR(A.`LTE-R_CODE`,1,11),A.lteCode AS `LTE-R_CODE` , COUNT(A.lteCode) AS CNT ,B.* FROM (
SELECT `LTE-R_CODE`,SUBSTR(`LTE-R_CODE`,9,3) AS lteCode,USE_YN FROM tbl_router_info T
)A LEFT JOIN tbl_depart_info B ON(SUBSTR(A.`LTE-R_CODE`,1,11)=B.DEPART_CODE) GROUP BY A.lteCode ORDER BY CNT DESC

WITH RAN AS ( SELECT * FROM tbl_rsrp_strength ) SELECT B.KEY_VAL,COUNT(B.KEY_VAL) AS cnt FROM ( SELECT T1.*,T2.* FROM (SELECT `LTE-R_IP`,`LTE-R_MAC_ADD`,`LTE-R_CODE`,SUBSTR(`LTE-R_CODE`,9,3) AS lteCode FROM tbl_router_info) T1 LEFT JOIN tbl_depart_info T2 ON SUBSTR(T1.`LTE-R_CODE`,1,11)=T2.DEPART_CODE )A LEFT JOIN (SELECT `LTE-R_IP` AS K1,`LTE-R_MAC_ADD` AS K2, CASE WHEN `LTE-R_RSRP_VAL` > RAN.EXCELLENT_VAL THEN 'EXCELLENT' WHEN `LTE-R_RSRP_VAL` > RAN.GOOD_VAL THEN 'GOOD' WHEN `LTE-R_RSRP_VAL` > RAN.MEDIUM_VAL THEN 'MIDDLE' ELSE 'WEAK' END AS KEY_VAL FROM tbl_last_data,RAN)B ON A.`LTE-R_IP`=B.K1 AND A.`LTE-R_MAC_ADD`=B.K2 WHERE 1=1
AND A.DEPART_CODE=#{departCode}
GROUP BY b.key_val HAVING KEY_VAL IS NOT NULL

WITH RAN AS ( SELECT * FROM tbl_rsrq_strength ) SELECT B.KEY_VAL,COUNT(B.KEY_VAL) AS cnt FROM ( SELECT T1.*,T2.* FROM (SELECT `LTE-R_IP`,`LTE-R_MAC_ADD`,`LTE-R_CODE`,SUBSTR(`LTE-R_CODE`,9,3) AS lteCode FROM tbl_router_info) T1 LEFT JOIN tbl_depart_info T2 ON SUBSTR(T1.`LTE-R_CODE`,1,11)=T2.DEPART_CODE )A LEFT JOIN (SELECT `LTE-R_IP` AS K1,`LTE-R_MAC_ADD` AS K2, CASE WHEN `LTE-R_RSRQ_VAL` > RAN.EXCELLENT_VAL THEN 'EXCELLENT' WHEN `LTE-R_RSRQ_VAL` > RAN.GOOD_VAL THEN 'GOOD' WHEN `LTE-R_RSRQ_VAL` > RAN.MEDIUM_VAL THEN 'MIDDLE' ELSE 'WEAK' END AS KEY_VAL FROM tbl_last_data,RAN)B ON A.`LTE-R_IP`=B.K1 WHERE 1=1
AND A.DEPART_CODE=#{departCode}
GROUP BY b.key_val HAVING KEY_VAL IS NOT NULL

SELECT SUBSTR(`LTE-R_CODE`,1,11) AS `LTE-R_CODE` ,COUNT(SUBSTR(`LTE-R_CODE`,1,11)) AS `TEAM_CNT` ,(SELECT COUNT(*) FROM tbl_router_info) AS `DEVICE_CNT` FROM tbl_router_info
WHERE SUBSTR(`LTE-R_CODE`,1,11)=#{departCode}
GROUP BY SUBSTR(`LTE-R_CODE`,1,11)
